<?phpnamespace App\Helpers;trait ServiceHelper {    function getService($url){        $ch = curl_init($url);        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);        curl_setopt($ch, CURLOPT_TIMEOUT,600);        $return = curl_exec($ch);        if(!curl_errno($ch))        {            $info = curl_getinfo($ch);            echo 'curl_info', 'İsteğin ' . $info['url'] . ' adresine gönderilmesi ' . $info['total_time'] . ' saniye sürdü';        }        curl_close($ch);        $result = json_decode($return);        return $result;    }}